import { ProductsContext } from "../../contexts/products.context.js";
import { useContext } from "react";

const Shop = () => {
  const products = useContext(ProductsContext);
  return SHOP_DATA.map((item) => {
    return (
      <div key={item.id}>
        <h1>{item.name}</h1>
        <img src={item.imageUrl} alt={`image is: ${item.name}`} />
        <h2>{`$ ${item.price}`}</h2>
      </div>
    );
  });
};

export default Shop;
